import React, {useState, useEffect} from "react";
import axios from "axios";

const ChickenForm = () => {
    
    const [chickenName, setChickenName] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    
    const chickenInfo = {chickenName, description, price} 
    
    const 제출버튼 = () => {
        axios.post("http://localhost:9090/api/chicken",chickenInfo)
        .then(response => {
            setData(response.data);
            alert("제출완료");
        })
        .catch(e => alert("에러 발생!!"));
    }

    return (
        <div className="chickenform-container">
            <label>메뉴 이름 :
                <input type="text" value={chickenName} onChange={(e) => setChickenName(e.target.value)} />
            </label>
            
            <label>메뉴 설명 :
                <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
            </label>
            
            <label>가격 :
                <input type="number" value={price} onChange={(e) => setPrice(e.target.value)}/>
            </label>

            <button onClick={제출버튼}>등록하기</button>
            <button>메인으로 돌아가기</button>
           
            
        </div>
    )
}
export default ChickenForm;
